import React from 'react'
import Logo from '../../imgs/logo.png'
import EditEmployee from '../content/usermanagement/editemployee'
import EditTagEmployee from '../content/tagmanagement/edittagemployee'

export default class Modal extends React.Component {
    display = 'none';
    opacity = 0;
    isShow = false;
    data = null;
    constructor(props) {
        super(props);
        this.state = {
            isRerender: false,
            display: 'none',
            opacity: 0,
            data: null,
        }
        this.exportUserRef = React.createRef();
        //this.exportUserRef = React.createRef();
        this.exportControlerRef = React.createRef();
        this.exportLockerStatusRef = React.createRef();
        this.exportLockerHistoryRef = React.createRef();
        this.exporMapUserRef = React.createRef();
    }

    onCancelButtonClick = () => {
        setTimeout(() => {
            this.setState({
                display: 'none',
                width: 300,
            })
        }, 200);

        this.setState({
            opacity: 0,
        })
    }

    onModalShow = (data) => {
        this.data = data;
        this.setState({
            display: 'flex',
        });
        setTimeout(() => {
            this.setState({
                opacity: 1,
            });
        }, 50);
    }

    onChangeStatus = (type, code, res) => {
        switch (type) {
            default:
                break;
        }
    }

    renderContent = () => {
        switch (this.props.type) {
            case 'editEmployee':
                return <EditEmployee onCancelClick={this.onCancelButtonClick}
                    data={this.data}/>
            case 'editTagEmployee':
                return <EditTagEmployee onCancelClick={this.onCancelButtonClick} 
                data={this.data}/>
            default:
                break;
        }
    }

    render() {
        let mainContainer = {
            display: this.state.display,
            width: '100%',
            height: '95%',
            alignItems: 'center',
            justifyContent: 'center',
            position: 'fixed',
            zIndex: 15,
            opacity: this.state.opacity,
            transition: 'opacity .2s'
        };

        let mainSubContainer = {
            display: this.state.display,
            width: '100%',
            height: '100%',
            alignItems: 'center',
            justifyContent: 'center',
            position: 'relative',
        };

        let mainContent = {
            display: 'flex',
            flexDirection: 'column',
            width: this.props.type === 'mapExistedLocker' ? '50%' : 300,
            // height: this.props.type === 'mapExistedLocker' ? '90%' : 'auto',
            maxHeight: '90%',
            backgroundColor: 'white',
            border: '.1vh solid red',
            borderRadius: '1vh',
            WebkitBoxShadow: '0.2vh .2vh .5vh 0vh rgba(0,0,0,0.75)',
            MozBoxShadow: '.2vh .2vh .5vh 0vh rgba(0,0,0,0.75)',
            boxShadow: '.2vh .2vh .5vh 0 rgba(0,0,0,0.75)',
            padding: '10px 20px 0px 20px',
            position: 'relative',
            transition: 'max-height .5s'
        };

        return (
            <div style={mainContainer}>
                <div style={mainSubContainer}>
                    <div style={styles.mainBlurBackground}>
                    </div>
                    <div style={mainContent}>
                        <div style={styles.logoContainer}>
                            <img src={Logo} style={{ width: '100%', height: '100%' }} alt='' />
                        </div>
                        <div style={styles.logoSub}>
                        </div>
                        {this.renderContent()}
                    </div>
                </div>
            </div>
        )
    }
}

const styles = {
    logoContainer: {
        position: 'absolute',
        width: 106,
        height: 39,
        top: -28,
        display: 'flex',
        justifyContent: 'center',
        left: 13,
        zIndex: 3,
    },

    logoSub: {
        position: 'absolute',
        width: 97,
        height: 2,
        backgroundColor: 'white',
        zIndex: 1,
        top: -1,
    },

    mainSubContainer: {
        display: 'flex',
        width: '100%',
        height: '100%',
        alignItems: 'center',
        justifyContent: 'center',
        position: 'relative',
    },

    mainBlurBackground: {
        position: 'absolute',
        width: '100%',
        height: '100%',
        backgroundColor: '#ccc',
        zIndex: -3,
        opacity: .7
    },

    mainHeader: {
        fontSize: '3vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        height: 30
    },

    mainForm: {
        marginTop: 15,
        flex: 1
    },

    mainFooter: {
        marginTop: 70,
        height: 70,
        borderTop: '1px solid red',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
    }
}
