import React, { Fragment } from 'react';
import MaterialTable from 'material-table';
import LokerStatusReport from '../../../core/report';
import TableIcons from '../../common/materialicon';
import Input from '../../common/input';
import Combobox from '../../common/combobox';
import Button from '../../common/button';


export default class LockerStatusReport extends React.Component {
    labelInput = '';
    lstatus = 0;
    constructor(props) {
        super(props);
        this.state = {
        };
        this.tableRef = React.createRef();
    }
    columns = [
        { title: 'Tòa nhà', field: 'bName' },
        { title: 'Tầng', field: 'lLv' },
        { title: 'Tủ', field: 'lLabel' },
        { title: 'Trạng thái', field: 'status', render: rowData => {
            if(rowData.status === 'FREE') {
                return (
                    <span style={{color: 'green'}}>
                        Tủ trống
                    </span>
                )
            }
            else if(rowData.status === 'OCCUPIED') {
                return (
                    <span style={{color: 'red'}}>
                        Tủ đang được sử dụng
                    </span>
                )
            }
            else if( rowData.status === 'DISABLE') {
                return (
                    <span style={{color: 'gray'}}>
                        Tủ bị vô hiệu hóa
                    </span>
                )
            }
            else {
                return (
                    <span style={{color: 'gray'}}>
                        Tủ có lỗi kĩ thuật
                    </span>
                )
            }
        } },
        { title: 'Người sử dụng cuối cùng', field: 'eName' },
        { title: 'ID nhân sự', field: 'eCode' },
        { title: 'Thời điểm thao tác lần cuối', field: 'aTime' },
    ]

    options = {
        search: false,
        pageSize: 20,
        pageSizeOptions: [20],

    }

    getDataFromServer = (query) => {
        return new Promise((resolve, reject) => {
            LokerStatusReport.getStatusReport(0,0, '', this.labelInput, this.lstatus , query.page + 1 )
                .then(value => {
                    resolve({
                        data: value.items,
                        page: value.currentPage - 1,
                        totalCount: value.total,
                    }) 
                })
        })
    }

    onLabelChange = (value) => 
    { 
        this.labelInput = value;
    }

    onStatusChange = (value) => {
        if(value === 'Tủ trống') {
            this.lstatus = 1;
        }
        else if (value === 'Tủ đang sử dụng') {
            this.lstatus = 2;
        }
        else if( value === 'Tủ bị vô hiệu hóa'){
            this.lstatus = 4;
        }
        else {
            this.lstatus = 3;
        }
        console.log(this.lstatus);
    }
    onSearchClick = () => {
        this.tableRef.current && this.tableRef.current.onQueryChange();
        // if (typeof this.tableRef.current !== 'undefined'){
        //     this.tableRef.current.onQueryChange();
        // }
    }


    componentDidMount() {
    }

    render() {
        return(
            <Fragment>
                <div style={{padding: '10px'}}>
                    <h2>Báo cáo trạng thái các tủ đang hoạt động trong hệ thống</h2>
                </div>
                <div style={{display: 'flex', margin: '10px' }}>
                    <Combobox
                     classes = {'combobox-style'}
                     title={'Tòa nhà'} 
                     />
                    <Combobox
                     title={'Tầng'} 
                     />
                    <Combobox 
                    title={'Thiết bị quản lí'} 
                    />
                    <Input 
                        title={'Nhãn tủ'} 
                        onChange = {this.onLabelChange
                    }
                    />
                    <Combobox 
                    title={'Trạng tháitủ'} 
                    data= {[
                        'Tủ trống',
                        'Tủ đang sử dụng',
                        'Tủ bị vô hiệu hóa',
                        'Tủ có lỗi kỹ thuật'
                    ]}
                    onChange = {this.onStatusChange}
                    />   
                    <Button title={'Tìm kiếm'}
                        onClick={this.onSearchClick} />
                </div>
                <MaterialTable 
                tableRef={this.tableRef}
                title = 'Danh sách các tủ đang hoạt động trong hệ thống'
                columns = {this.columns}
                options = {this.options}
                icons= {TableIcons}
                data = {this.getDataFromServer}
                />
            </Fragment>
        )
    }
}
