import React, { Fragment } from 'react';
import TableIcons from '../../common/materialicon'
import Locker from '../../../core/locker'
import Building from '../../../core/building'
import Level from '../../../core/level'
import Controller from '../../../core/controller';
import Combobox from '../../common/combobox';
import Input from '../../common/input';
import Button from '../../common/button';
import FButton from '../../common/floatbutton'
import Add from '@material-ui/icons/Add'
import { ContextMenu, MenuItem, ContextMenuTrigger } from "react-contextmenu";

export default class LockerManagement extends React.Component {
    rBuildingIdInput = 0;
    rLevelIdInput = 0;
    rImeiInput = '';
    rLabelInput = '';
    lPage = [];
    bDatas = [];
    lDatas = [];
    cDatas = [];
    array1 = {};
    DictBName = {};
    Build = [];
    DictBId = {};
    DictLvName = {};
    DictLvId = {};
    Level = [];
    DictBLvIName = {};
    DictBLvIId = {};
    Imei = [];
    Test1 = [];
    Lk = [];

    BIdInput= 0;
    LvIdInput= 0;
    BLvIInput= '';
    LabelInput= '';
    Lockers = [];

    constructor(props) {
        super(props);
        this.state = {
            isShowLock: false,
            PageLokers1: [],
            PageLokers2: [],

        };
        this.tableRef = React.createRef();
    }

    onChangeBLvI = (value) => {
        this.BLvIInput=this.DictBLvIId[value]
    }

    onChangeBLevel = (value) => {
        this.LvIdInput = this.DictLvId[value]
    }

    onChangeBuild = (value) => {
        this.BIdInput= this.DictBId[value]
    }

    onLabelChange = (value) => {
       this.LabelInput = value;
    }

    onSearchClick = () => {
        this.setState({
            isShowLock: true,
        })
        Locker.getLocker(this.BIdInput, this.LvIdInput, this.BLvIInput, this.LabelInput, 0, 0, 1)
            .then(value => {
                if (value.items !== undefined) {
                    if (value.items.length > 0) {
                        // value.items.map((item, index) => {
                        //     this.Lockers[index] = item;
                            
                        // })
                        this.setState({
                            PageLokers1: value.items,
                        })
                    }
                    
                }
            })
            Locker.getLocker(this.BIdInput, this.LvIdInput, this.BLvIInput, this.LabelInput, 0, 0, 2)
            .then(value => {
                if (value.items !== undefined) {
                    if (value.items.length > 0) {
                        // value.items.map((item, index) => {
                        //     this.Lockers[index] = item;
                            
                        // })
                        this.setState({
                            PageLokers2: value.items,
                        })
                    }
                    
                }
            })
       // console.log(this.Lockers);

       
    }
    handleClick = (e, data) =>{
        console.log(data.foo);
      }

    componentDidMount() {
        Building.getBuilding()
            .then(value => {
                if (value) {
                    if (value.items.length > 0) {
                        value.items.map((item, index) => {
                            this.DictBName[item.bId] = item.bName;
                            this.DictBId[item.bName] = item.bId;
                            this.Build[index] = item.bName;
                        })
                    }
                }
            })

        Level.getLevel()
            .then(value => {
                if (value) {
                    if (value.items.length > 0) {
                        value.items.map((item, index) => {
                            let bLv = ' Tòa ' + item.bName + ' - Tầng ' + item.lLv;
                            this.DictLvName[item.lId] = bLv;
                            this.DictLvId[bLv] = item.lId;
                            this.Level[index] = bLv;
                        })

                    }
                }
            })

        Controller.getController()
            .then(value => {
                if (value) {
                    if (value.items.length > 0) {
                        value.items.map((item, index) => {
                            let bLvImei = ' Tòa ' + item.bName + ' - Tầng ' + item.lLv + '- quản lý cụm tủ: ' + item.sLabel + ' đến ' + item.eLabel;
                            this.DictBLvIName[item.imei] = bLvImei;
                            this.DictBLvIId[bLvImei] = item.imei;
                            this.Imei[index] = bLvImei;
                        })
                    }
                }
            })
    }

    render() {
        console.log('render')
        let cot = 0;
        let hang = 0;
        let ManageLocker1 = [];
        let ManageLocker2 = [];

        let pagelockers1= this.state.PageLokers1;
        let pagelockers2 = this.state.PageLokers2;
        if(pagelockers1[0] === pagelockers2[0]){
            pagelockers2 = [];
        }
        let counter = 0;

        for (hang = 0; hang < 3; hang++) 
        {
            for (cot = 0; cot < 7; cot++) 
            {
                pagelockers1.map((item, index) =>
                {
                    if ( item.lRw === hang && item.lCl === cot) {
                        ManageLocker1.push(
                        <div>
                            <ContextMenuTrigger id="some_unique_identifier">
                                <div style={styles.locker}>
                                    {item.lLb}
                                </div>
                            </ContextMenuTrigger>
                            <ContextMenu id="some_unique_identifier">
                            <MenuItem data={{foo: 'bar'}} onClick={this.handleClick}>
                            ContextMenu Item 1
                            </MenuItem>
                            <MenuItem data={{foo: 'bar'}} onClick={this.handleClick}>
                            ContextMenu Item 2
                            </MenuItem>
                            <MenuItem divider />
                            <MenuItem data={{foo: 'bar'}} onClick={this.handleClick}>
                            ContextMenu Item 3
                            </MenuItem>
                            </ContextMenu>
                        </div>
                        
                        );
                        counter = 1
                    }
                    // else {
                    //     console.log('ko')
                    //     // ManageLocker.push(
                    //     //     <div>

                    //     //     </div>
                    //     // )

                    // }
                })
                if(counter === 0) {
                    ManageLocker1.push(
                        <div>

                        </div>
                    )
                }

            }

        }

        for (hang = 0; hang < 3; hang++) 
        {
            for (cot = 0; cot < 7; cot++) 
            {
                pagelockers2.map((item, index) =>
                {
                    if ( item.lRw === hang && item.lCl === cot) {
                        ManageLocker2.push(
                            <div style={styles.locker}>
                                {item.lLb}
                            </div>
                        );
                        counter = 1
                    }
                })
                if(counter === 0) {
                    ManageLocker2.push(
                        <div>

                        </div>
                    )
                }

            }

        }

        return (
            <Fragment>
                <div style={{ margin: '30px 10px 40px 20px' }}>
                    <h2>
                        Quản lý tủ
                  </h2>
                </div>
                <div className="around">

                    <div className="title-style">
                        <span>
                            Danh sách các tòa nhà trong hệ thống
                            </span>
                    </div>
                    <div className="div-control" style={{ display: 'flex' }}>
                        <Combobox
                            title={'Tòa nhà'}
                            data={this.Build}
                            onChange={this.onChangeBuild}
                        />

                        <Combobox
                            title={'Tầng'}
                            data={this.Level}
                            onChange={this.onChangeBLevel}

                        />

                        <Combobox
                            title={'Thiết bị quản lý'}
                            data={this.Imei}
                            onChange={this.onChangeBLvI}
                        />

                        <Input
                            title={'Nhãn tủ'}
                            onChange={this.onLabelChange
                            }
                        />
                        <Button
                            title={'Tìm kiếm'}
                            onClick={this.onSearchClick}
                        />
                    </div>

                    <div style={styles.divLock}>
                        {/* {ManageLocker} */}
                        {ManageLocker1.map((item) => {
                            return (
                                <div>{item}</div>
                            )
                        })}
                    </div>
                    <div style={styles.divLock}>
                        {ManageLocker2.map((item) => {
                            return (
                                <div>{item}</div>
                            )
                        })}
                    </div>
                    </div>
                <FButton title={'aaaa'}
                    icon={<Add/>}
                    position={['auto', 20, 'auto', 100]} />
            </Fragment>
        )
    }
}

const styles = {

    locker: {
        border: '1px solid white',
        fontSize: '15px',
        padding: '10px',
        textAlign: 'center',
        minHeight: '120px',
        background: 'rgb(76, 175, 80)',
        color: 'white',
        fontWeight: 600,
        cursor: 'pointer',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'column',
    },

    divNull: {
        border: '1px solid white',
        fontSize: '15px',
        padding: '10px',
        textAlign: 'center',
        minHeight: '120px',
        color: 'white',
        fontWeight: 600,
        cursor: 'pointer',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'column',
    },


    divLock: {
         gridTemplateColumns: 'repeat(7, 1fr)',
         gridTemplateRows: 'repeat(3, 1fr)',
        display: 'grid',
        marginTop: '30px',
    }

}
