import React, { Fragment } from 'react'
import Button from '../../common/button'
import Input from '../../common/input'
import ResponseCode from '../../../staticresources/responsecode'
import User from '../../../core/user'



export default class EditTagEmployee extends React.Component{
    tagNew = '';
    emailNew ='';
    constructor(props){
        super(props);
        this.state={

        }
    }
    
    onCancelClick = () => {
       // this.props.data.callback('xin chào');
        this.props.onCancelClick();
    }

    onAcceptClick = () => {
        console.log(typeof this.props.data.empData.eCode)
        console.log( typeof this.props.data.empData.name.type)
        console.log( typeof this.emailNew)
        console.log( typeof this.props.data.empData.dId.type)
        console.log( typeof this.tagNew)
        let emailNew = this.props.data.empData.email;
        if(this.emailNew){
            emailNew = this.emailNew;
        }

        return new Promise((resolve, reject) => {
            User.editUser(this.props.data.empData.eCode, this.props.data.empData.name, emailNew, this.props.data.empData.dId, this.tagNew)
                .then(res => {
                    if (res) {
                        if (res.code && res.code === ResponseCode.modify_emp_success) {
                            resolve();
                        }
                    }
                    resolve();
                })
        });
    }

    onChangeTag  = (value) => {
        this.tagNew = value
    }

    onChangeEmail = (value) => {
        this.emailNew = value;
    }

    onEditTableRow = () => {
       
    }

    componentDidMount() {
        console.log(this.props.data.empData)
    }

    render(){
        return(
          <Fragment>
                <div style={styles.TitleModal} >
                    Thay đổi thông tin sử dụng thẻ của nhân viên
                </div>
                <div>
                   
                    <Input 
                    title={'Tên nhân viên'}
                    disabled
                    readonly ={true}
                    value={this.props.data.empData.name}
                    />
                   
                    <Input
                    title={'ID nhân sự'}
                    disabled
                    readonly ={true}
                    value= {this.props.data.empData.eCode}
                    />
                    <Input
                    title={'Email'}
                    value= {this.props.data.empData.email}
                    onChange={this.onChangeEmail}
                    type={"text"}
                    />
                    <Input
                    title={'Mã thẻ từ cũ'}
                    disabled
                    readonly ={true}
                    value= {this.props.data.empData.rCode}
                    />
                    <Input
                    title ={'Mã thẻ từ mới'}
                    onChange={this.onChangeTag}
                    />
                </div>
                <div style={styles.GroupButton}>
                    <div style={styles.ButtonStyle}>
                        <Button onClick={this.onCancelClick}
                        title={'Hủy'}/>
                    </div>
                    <div style={styles.ButtonStyle}>
                        <Button 
                        title={'Đồng ý'}
                        onClick={this.onAcceptClick}
                        />

                    </div>
                   
                </div>
              
          </Fragment>
        )
    }
}

const styles = {

    TitleModal: {
        fontSize: '21px',
        fontWeight: 600,
        color: 'rgb(227, 6, 19)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        height: '30px',
        paddingTop: '25px',
        paddingBottom: '15px',
        textAlign: 'center'
    },

    GroupButton: {
        marginTop: '50px',
        height: '70px',
        borderTop: '1px solid red',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
    },

    ButtonStyle: {
        width: '47%',
    }

}
