import React, { Fragment } from 'react';
import MaterialTable from 'material-table';
import TableIcons from '../../common/materialicon';
import Input from '../../common/input';
import Combobox from '../../common/combobox';
import Button from '../../common/button';
import DatePickers from '../../common/datepicker';
import Warning from '../../../core/warning';
import Building from '../../../core/building';
import Level from '../../../core/level';

export default class WarningManagement extends React.Component {
    DictBuilding = {};
    DictLevel ={};
    LabelInput ='';
    ActInput = '';
    TypeWInput = 0;
    Method ='';
    isRead = 0;

    DictBName = {};
    Build = [];
    DictBId = {};
    BIdInput = 0;



    columnsTable = [
        { title: 'Loại cảnh báo', field: 'tID', render: rowData => {
            if (rowData.tId === 2) {
                return(
                    <span style ={{color: 'red'}}>
                        Thiết bị mất kết nối
                    </span>
                )
            }
            else {
                return(
                    <span style ={{color: 'green'}}>
                        truy cập tủ trái phép
                    </span>
                )
            }

        } },
        { title: 'Hình thức', field: 'method', render: rowData => {
            if (rowData.tId === 2) {
                return(
                    <span>
                        
                    </span>
                )
            }
            else {
                return(
                    <span style ={{color: 'green'}}>
                        Không rõ
                    </span>
                )
            }

        } },
        { 
            title: 'Tòa nhà', 
            field: 'bId',
            render: rowData => {
                return(
                    <span>{this.DictBuilding[rowData.bId]}</span>
                )
            } 
        },
        { 
            title: 'Tầng', 
            field: 'lLv', 
            render: rowData => {
                return(
                    <span>
                        Tầng {this.DictLevel[rowData.lLv]}
                    </span>
                )
            }
        },
        { 
            title: 'Tủ', 
            field: 'label' 
        },
        { 
            title: 'Thiết bị quản lý',
             field: 'imei' 
        },
        { 
            title: 'Thời gian ghi nhận',
             field: 'wTime' 
        },
    ] 

    optionsTable = {
        search: false,
        pageSize: 20,
        pageSizeOptions: [20],
    }

    constructor(props) {
        super(props);
        this.state = {
            isUpdateView: false,
        };
        this.tableRef = React.createRef();
       
    }

    onLabelChange = (value) => {
        this.LabelInput = value;
    }
    
    onSearchClick = () => {
        this.tableRef.current && this.tableRef.current.onQueryChange();
    }

    getDataFromServer = (query) => {
        return new Promise((resolve, reject) => {
            Warning.getWarning(this.BIdInput, 0,'', this.LabelInput,'',this.TypeWInput, this.isRead,'','', query.page +1)
                .then(value => {
                    resolve({
                        data: value.items,
                        page: value.currentPage - 1,
                        totalCount: value.total,
                    })  
                })
        })
    }

    componentWillMount() {
        
    }

    onActChange = (value) => {
        
    }

    onIsReadChange = (value)=> {
        if (value === 'Đã đọc') {
            this.isRead = 2;
        }
        else {
            this.isRead = 1;
        }
    }

    onTypeWChange = (value) => {
        if(value === 'Truy cập trái phép') {
            this.TypeWInput = 1;
        }
        else {
            this.TypeWInput = 2;
        }
    }

    componentDidMount() {
        Building.getBuilding()
        .then(value => {
            if(value){
                if(value.items.length >0) {
                    value.items.map((item, index) => {
                        {this.DictBuilding[item.bId] = item.bName}
                    }) 
                }
                this.setState({
                    isUpdateView: !this.isUpdateView,
                });
            }
        });

        Level.getLevel()
        .then(value => {
            if(value){
                if(value.items.length >0) {
                    value.items.map((item, index) => {
                        {this.DictLevel[item.lId] = item.lLv}
                    }) 
                }
            }
        });

        Building.getBuilding()
            .then(value => {
                if(value) {
                    if(value.items.length >0) {
                        value.items.map((item,index) => {
                            this.DictBName[item.bId] = item.bName;
                            this.DictBId[item.bName] = item.bId;
                            this.Build[index] = item.bName; 
                        })
                    }
                } 
        })
    }

    onChangeBuild = (value) => {
        this.BIdInput = this.DictBId[value];
    }

    
    render() {
        return(
            <Fragment>
                <div style={{padding: '10px'}}>
                  <h2>Cảnh báo hệ thống</h2>
                </div>

                <div style={{display: 'flex' }}>
                    <Combobox
                        title={'Tòa nhà'} 
                        data = {this.Build}
                        onChange = {this.onChangeBuild}
                    /> 
                     <Combobox 
                    title={'Tầng'} 
                    />                  

                    <Combobox
                     title={'Thiết bị quản lý'} 
                     />
                    <Input 
                        title={'Nhãn tủ'} 
                        onChange = {this.onLabelChange }
                    />
                     <Combobox 
                    title={'Tác nhân'} 
                    data = {[
                        'Ra lệnh mở từ website quản trị',
                        'Mở trục tiếp bằng thẻ Admin',
                        'Không rõ'
                    ]}
                    onChange = {this.onActChange}
                    />
                    <Combobox 
                    title={'Loại cảnh báo'}
                    data ={[
                        'Truy cập trái phép',
                        'Thiết bị mất kết nối'
                    ]}
                    onChange = {this.onTypeWChange}
                    />                   
                </div>
                <div  style={{display: 'flex', width: '70%'}}>
                <Combobox 
                title={'Trạng thái đọc'}
                data = {[
                    'Đã đọc',
                    'Chưa đọc'
                ]}
                onChange = {this.onIsReadChange}

                />
                <DatePickers 
                title={'Ngày bắt đầu'}
                
                />
                <DatePickers 
                title ={'Ngày kết thúc'}
                />
                <Button 
                    title={'Tìm kiếm'}
                    onClick={this.onSearchClick} 
                />
                </div>

                <MaterialTable 
                tableRef={this.tableRef}
                options = {this.optionsTable}
                title= 'Danh sách các cảnh báo hệ thống'
                icons= {TableIcons}
                data = {this.getDataFromServer}
                columns = {this.columnsTable}
                />
            </Fragment>
        )
    }
}
