import React, { Fragment } from 'react';
import MaterialTable from 'material-table';
import TableIcons from '../../common/materialicon';
import Input from '../../common/input';
import Button from '../../common/button';
import Building from '../../../core/building';
import Controller from '../../../core/controller';
import Combobox from '../../common/combobox';

export default class ControllerManagemeent extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
        };
        this.tableRef = React.createRef();
       
    }

    columnsTable = [
        {
            title: 'Tòa nhà',
            field: 'bName'
        },
        {
            title: 'Tầng',
            field: 'lLv', 
            render: rowDate => {
                return(
                    <span>
                        Tầng {rowDate.lLv}
                    </span>
                )
            }
        },
        {
            title: 'Imei',
            field: 'imei',
        },
        {
            title: 'Địa chỉ MAC',
            field: 'mac',
        },
        {
            title: 'Khu vực',
            field: 'zone',
        },
        {
            title: 'Thời điểm phản hồi cuối cùng',
            field: 'heath',
        },
        {
            title: 'Phiên bản Firmware',
            field: '',
        },
        
        {
            title: 'Action',
            field: '',
        }
    ]

    optionsTable = {
        search: false,
        pageSize: 20,
        pageSizeOptions: [20],
    }

    getDataFromServer = (query) => {
        return new Promise((resolve, reject) => {
            Controller.getController()
            .then(value => {
                if(value){
                    resolve({
                        data: value.items,
                        page: value.currentPage - 1,
                        totalCount: value.total,
                     })
                }
                else {
                    resolve({
                        data: [],
                        page: 0,
                        totalCount:0,
                     })
                }
            })
        })
    }


    render() {
        return(
            <Fragment>
                <div>

                </div>
                <div style={{width: '85%', display: 'flex'}}>
                    <Combobox />
                    <Combobox/>
                    <Input />
                    <Input />
                    <Button />

                </div>
                <MaterialTable
                tableRef = {this.tableRef}
                columns = {this.columnsTable}
                options={this.optionsTable}
                icons = {TableIcons}
                data = {this.getDataFromServer}
                title = {'Danh sách thiết bị điều khiển hiện tại'}
                />
            </Fragment>
        );
    }
}
