import React, { Fragment } from 'react';
import MaterialTable from 'material-table';
import UserLoker from '../../../core/user';
import ManageLoker from '../../../core/locker';
import TableIcons from '../../common/materialicon';
import Department from '../../../core/department';
import Input from '../../common/input'
import Button from '../../common/button'
import Combobox from '../../common/combobox'



export default class ManageUserLockerManagement extends React.Component {
    dictDev ={};
    ENameInput = '';
    ECodeInput = '';
    LabelInput = '';
    LabelInputTable2 = '';

    constructor(props) {
        super(props);
        this.state = {
        
        };
        this.tableRef1 = React.createRef();
        this.tableRef2 = React.createRef();

        this.modalRef = React.createRef();
    
    }

    optionsTable = {
        search: false,
        pageSize: 20,
        pageSizeOptions: [20],

    }

    columnsTable1 = [
        {
             title: 'Họ và tên', 
             field: 'eName'
        },
        {
             title: 'ID nhân sụ', 
             field: 'eCode'
         },
        { 
            title: 'Bộ phận làm việc',
            field: 'dId', 
            render: rowData => {
                return(
                    <span>
                        {this.dictDev[rowData.dId]}
                    </span>
                )
            }
        },
        {
            title: 'Số tủ được phép sử dụng', 
            field:'lNum'
        },
        {
            title: 'Action', 
            field: ''
        }
    ]

    columnsTable2 = [
        {
            title: 'Tòa',
            field: 'bId'
        },
        {
            title: 'Tầng',
            field: 'lLv'
        },
        {
            title: 'Tủ',
            field: 'label'
        },
        {
            title: 'Action',
            field: ''
        }
    ]

    onSearchClick = () => {
        this.tableRef.current && this.tableRef.current.onQueryChange();
        // if (typeof this.tableRef.current !== 'undefined'){
        //     this.tableRef.current.onQueryChange();
        // }
    }

    getDataFromServer = (query) => {
        return new Promise((resolve, reject) => {
            UserLoker.getUsage(this.ENameInput, this.ECodeInput, this.LabelInput, 0, 0, query.page +1)
                .then(value => {
                    resolve({
                        data: value.items,
                        page: value.currentPage - 1,
                        totalCount: value.total,
                    })   
                })
        })
    }

    getLokerDataFromServer = (query) => {
        return new Promise((resolve, reject) => {
            ManageLoker.getManageLocker(0,0,'','', query.page +1)
                .then(value => {
                    if(value.items.length >0) {
                        resolve({
                            data: value.items,
                            page: value.currentPage - 1,
                            totalCount: value.total,
                        }) 

                    }
                    else {
                        resolve({
                            data: value.items,
                            page: value.currentPage - 1,
                            totalCount: 0,
                        }) 
                    }
                    
                })
        })
    }

    componentDidMount() {
        Department.getDepartment()
            .then(value => {
                if(value) {
                    if(value.items.length >0) {
                        value.items.map((item, index) => {
                            {this.dictDev[item.dId] = item.dName}
                        })
                    }
                    this.setState({
                        isUpdateView: !this.isUpdateView,
                    });
                }
       })
  
    }

    onENameChange = (value) => {
        this.ENameInput = value;
    }

    onECodeChange = (value) => {
        this.ECodeInput = value;
    } 

    onLabelChange = (value) => {
        this.LabelInput = value;
    }

    onLabelChange2 = (value) => {
        this.LabelInputTable2 = value;
    }

    onSearchClick1 = () => {
        this.tableRef1.current && this.tableRef1.current.onQueryChange();
    }

    onSearchClick2 = () => {
        this.tableRef2.current && this.tableRef2.current.onQueryChange();
    }
 
  
    render() {
        
        return(
            <Fragment>
                
                <div style={{display:'flex', margin: '0px 5px'}}>
                    <div style={{flex: '1'}}>
                        <div style={{display: 'flex', margin: '10px' }}>
                            <Input 
                                title={'Tên nhân viên'} 
                                onChange = {this.onENameChange}
                            />

                            <Input 
                                title={'ID nhân sự'} 
                                onChange = {this.onECodeChange}
                            />

                            <Combobox
                            title={'Bộ phận làm việc'} 
                            />

                            <Combobox
                            title={'Tòa nhà'} 
                            />

                            <Input 
                                title={'Nhãn tủ'} 
                                onChange = {this.onLabelChange
                            }
                            />

                            <Button title={'Tìm kiếm'}
                                onClick={this.onSearchClick1} />
                        </div>
                        <MaterialTable
                        tableRef={this.tableRef1}
                        options={this.optionsTable}
                        columns={this.columnsTable1}
                        data={this.getDataFromServer}
                        icons= {TableIcons}
                        title="Danh sách người dùng hiện tại"/>
                    </div>
                    <div  style={{flex: '1'}}>
                        <div style={{display: 'flex', margin: '10px' }}>
                            <Combobox
                            title={'Tòa nhà'} 
                            />

                            <Combobox
                            title={'Tầng'} 
                            />

                            <Combobox
                            title={'Thiết bị quản lý'} 
                            />

                            <Input 
                                title={'Nhãn tủ'} 
                                onChange = {this.onLabelChange2
                            }
                            />
                            <Button title={'Tìm kiếm'}
                                onClick={this.onSearchClick2} />
                        </div>
                    
                        <MaterialTable
                        tableRef={this.tableRef2}
                        options={this.optionsTable}
                        columns = {this.columnsTable2}
                        data={this.getLokerDataFromServer}
                        icons= {TableIcons}
                        title="Danh sách tủ hiện tại"/>
                    </div>
                </div>
            </Fragment>
        )
    }
}
