import React, { Fragment } from 'react'
import Button from '../../common/button'
import Combobox from '../../common/combobox'
import Input from '../../common/input'
import ResponseCode from '../../../staticresources/responsecode'
import Controller from '../../../core/controller'
import Building from '../../../core/building';
import Level  from '../../../core/level';

export default class EditController extends React.Component{

    tagNew = '';
    DictBName = {};
    Build = [];
    DictBId = {};
    DictLvName = {};
    DictLvId ={};
    Level =[];

    constructor(props){
        super(props);
        this.state={
            imeiNew: this.props.data.ctlData.imei,
            macNew: this.props.data.ctlData.mac,
            zoneNew: this.props.data.ctlData.zone,
            BIdNew: this.props.data.ctlData.bId,
            LvIdNew: this.props.data.ctlData.lId,
        }
        this.comboboxRef = React.createRef();
    }
    
    onCancelClick = () => {
        this.props.onCancelClick();
    }

    onAcceptClick = () => {

        let cId = this.props.data.ctlData.cId;
        let des =  this.props.data.ctlData.des;
        let bId = this.state.BIdNew;
        let lId = this.state.LvIdNew;
        let imei = this.state.imeiNew;
        let mac = this.state.macNew;
        let zone = this.state.zoneNew;


        return new Promise((resolve, reject) => {
            Controller.editController(cId, des, bId, lId, imei, mac, zone)
                .then(res => {
                    if (res) {
                        if (res.code && res.code === ResponseCode.modify_ctl_success)
                        {
                            resolve();
                            this.props.data.callback();
                        }
                        else {
                            this.props.data.callbackFail();
                        }
                    }
                    resolve();
                })
        });
    }

    onChangeTag  = (value) => {
        this.tagNew = value
    }

    onChangeEmail = (value) => {
       this.setState({
           emailNew2: value,
       })
    }

    componentDidMount() {
        console.log(this.props.data);
        Building.getBuilding()
            .then(value => {
                if(value) {
                    if(value.items.length >0) {
                        value.items.map((item,index) => {
                            this.DictBName[item.bId] = item.bName;
                            this.DictBId[item.bName] = item.bId;
                            this.Build[index] = item.bName; 
                        })
                    }
                } 
        })

        Level.getLevel()
        .then(value => {
            if(value){
                if(value.items.length >0) {
                    value.items.map((item, index) => {
                        let bLv = ' Tòa ' + item.bName + ' - Tầng '  + item.lLv;
                        this.DictLvName[item.lId] = bLv;
                        this.DictLvId[bLv] = item.lId;
                        this.Level[index] = bLv ;
                    }) 
                }
            }
        });
    }

    onChangeMac = (value) => {
        this.setState({
            macNew: value,
        })
    }

    onChangeImei = (value) => {
        this.setState({
            imeiNew: value,
        })
    }

    onChangeZone =(value) => {
        this.setState({
            zoneNew: value,
        })
    }

    onChangeBLevel = (value) => {
        this.setState({
            LvIdNew: this.DictLvId[value],
        })
    } 

    onChangeBuild = (value) => {
        this.setState({
            BIdNew: this.DictBId[value]
        })
    }
    componentDidMount(){
        this.comboboxRef.current && this.comboboxRef.onUpdateDefaultValue(this.props.data.ctlData.bName)
    }

    UNSAFE_componentWillReceiveProps(newProps){
        console.log(newProps);
    }

    render(){

        let cId = this.props.data.ctlData.cId;
        let des =  this.props.data.ctlData.des;
        let bId = this.props.data.ctlData.bId;
        let lId = this.props.data.ctlData.lId;
        let imei = this.state.imeiNew;
        let mac = this.state.macNew;
        let zone = this.state.zoneNew;
        let bName = this.props.data.ctlData.bName;
        let lLv = this.props.data.ctlData.lLv;

        return(
            <Fragment>
                <div style={styles.TitleModal} >
                    Thay đổi thông tin thiết bị điều khiển
                </div>
                <div style={{marginTop: '30px'}}>
                    <Input 
                    title={'IMEI'}
                    value={imei}
                    onChange={this.onChangeImei}
                    />
                   
                    <Input
                    title={'Địa chỉ MAC'}
                    value= {mac}
                    onChange={this.onChangeMac}
                    />
                    <Combobox
                    ref={this.comboboxRef}
                    //value={bName}
                    title={'Tòa'}
                    data = {this.Build}
                    onChange = {this.onChangeBuild}
                    />
                    <Combobox
                    value={lLv}
                    title={'Tầng'}
                    data = {this.Level}
                    onChange = {this.onChangeBLevel}
                    />
                    <Input
                    title ={'Khu vực'}
                    value={zone}
                    onChange={this.onChangeZone}
                    />
                </div>
                <div style={styles.GroupButton}>
                    <div style={styles.ButtonStyle}>
                        <Button onClick={this.onCancelClick}
                        title={'Hủy'}/>
                    </div>
                    <div style={styles.ButtonStyle}>
                        <Button 
                        title={'Đồng ý'}
                        onClick={this.onAcceptClick}
                        />
                    </div>
                </div>
            </Fragment>
        )
    }
}

const styles = {

    TitleModal: {
        fontSize: '21px',
        fontWeight: 600,
        color: 'rgb(227, 6, 19)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        height: '30px',
        paddingTop: '25px',
        paddingBottom: '15px',
        textAlign: 'center'
    },

    GroupButton: {
        marginTop: '50px',
        height: '70px',
        borderTop: '1px solid red',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
    },

    ButtonStyle: {
        width: '47%',
    }

}
