import React, { Fragment } from 'react'
import Button from '../../common/button'
import Combobox from '../../common/combobox'
import Input from '../../common/input'
import ResponseCode from '../../../staticresources/responsecode'
import User from '../../../core/user'

export default class EditController extends React.Component{

    tagNew = '';

    DictBName = {};
    Build = [];
    DictBId = {};
   // BIdInput = 0; 

    DictLvName = {};
    DictLvId ={};
    Level =[];
    //LvIdInput = 0;


    constructor(props){
        super(props);
        this.state={
            //emailNew2: this.props.data.empData.email,
            imeiNew: this.props.data.ctlData.imei,
            macNew: this.props.data.ctlData.mac,
            zoneNew: this.props.data.ctlData.zone,
            BIdNew: this.props.data.ctlData.bId,
            LvIdNew: this.props.data.ctlData.lId,
        }
    }
    
    onCancelClick = () => {
        this.props.onCancelClick();
    }

    onAcceptClick = () => {

        // let cId = this.props.data.ctlData.cId;
        // let name =  this.props.data.ctlData.name;
        // let dId = this.props.data.ctlData.dId;

        // return new Promise((resolve, reject) => {
        //     User.editUser(eId, name, this.state.emailNew2, dId , this.tagNew)
        //         .then(res => {
        //             if (res) {
        //                 if (res.code && res.code === ResponseCode.modify_emp_success)
        //                 {
        //                     resolve();
        //                     this.props.data.callback();
        //                 }
        //             }
        //             resolve();
        //         })
        // });
        console.log(this.props.data);
    }

    onChangeTag  = (value) => {
        this.tagNew = value
    }

    onChangeEmail = (value) => {
       // this.emailNew = value;
       this.setState({
           emailNew2: value,
       })
    }

    componentDidMount() {
        Building.getBuilding()
            .then(value => {
                if(value) {
                    if(value.items.length >0) {
                        value.items.map((item,index) => {
                            this.DictBName[item.bId] = item.bName;
                            this.DictBId[item.bName] = item.bId;
                            this.Build[index] = item.bName; 
                        })
                    }
                } 
        })

        Level.getLevel()
        .then(value => {
            if(value){
                if(value.items.length >0) {
                    value.items.map((item, index) => {
                        let bLv = ' Tòa ' + item.bName + ' - Tầng '  + item.lLv;
                        this.DictLvName[item.lId] = bLv;
                        this.DictLvId[bLv] = item.lId;
                        this.Level[index] = bLv ;
                    }) 
                }
            }
        });
    }

    onChangeMac = (value) => {
        this.setState({
            macNew: value,
        })
    }

    onChangeImei = (value) => {
        this.setState({
            imeiNew: value,
        })
    }

    onChangeZone =(value) => {
        this.setState({
            zoneNew: value,
        })
    }

    onChangeBLevel = (value) => {
        this.setState({
            LvIdNew: this.DictLvId[value],
        })
    } 

    onChangeBuild = (value) => {
        this.setState({
            BIdNew: this.DictBId[value]
        })
    }

    render(){

        let cId = this.props.data.ctlData.cId;
        let des =  this.props.data.ctlData.des;
        let bId = this.props.data.ctlData.bId;
        let lId = this.props.data.ctlData.lId;
        let imei = this.props.data.ctlData.imei;
        let mac = this.props.data.ctlData.mac;
        let zone = this.props.data.ctlData.zone;

        return(
            <Fragment>
                <div style={styles.TitleModal} >
                    Thay đổi thông tin sử dụng thẻ của nhân viên
                </div>
                <div>
                    <Input 
                    title={'IMEI'}
                    value={imei}
                    onChange={this.onChangeImei}
                    />
                   
                    <Input
                    title={'Địa chỉ MAC'}
                    value= {mac}
                    onChange={this.onChangeMac}
                    />
                    <Combobox
                    title={'Tòa'}
                    data = {this.Build}
                    onChange = {this.onChangeBuild}
                    />
                    <Combobox
                    title={'Tầng'}
                    data = {this.Level}
                    onChange = {this.onChangeBLevel}
                    />
                    <Input
                    title ={'Khu vực'}
                    value={zone}
                    onChange={this.onChangeZone}
                    />
                </div>
                <div style={styles.GroupButton}>
                    <div style={styles.ButtonStyle}>
                        <Button onClick={this.onCancelClick}
                        title={'Hủy'}/>
                    </div>
                    <div style={styles.ButtonStyle}>
                        <Button 
                        title={'Đồng ý'}
                        onClick={this.onAcceptClick}
                        />
                    </div>
                </div>
            </Fragment>
        )
    }
}

const styles = {

    TitleModal: {
        fontSize: '21px',
        fontWeight: 600,
        color: 'rgb(227, 6, 19)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        height: '30px',
        paddingTop: '25px',
        paddingBottom: '15px',
        textAlign: 'center'
    },

    GroupButton: {
        marginTop: '50px',
        height: '70px',
        borderTop: '1px solid red',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
    },

    ButtonStyle: {
        width: '47%',
    }

}
