import React, { Fragment } from 'react';
import MaterialTable from 'material-table';
import TableIcons from '../../common/materialicon';
import Input from '../../common/input';
import Button from '../../common/button';
import Building from '../../../core/building';
import Level from '../../../core/level';
import Controller from '../../../core/controller';
import Combobox from '../../common/combobox';
import Department from '../../../core/department';

export default class DepartmentManagement extends React.Component {

    DepInput = '';

    constructor(props) {
        super(props);
        this.state = {
        };
        this.tableRef = React.createRef();
    }

    columnsTable = [
        {
            title: 'Tên', 
            field: 'dName',
        },
        {
            title:  'Action',
            field: '',
        }
    ]

    optionsTable = {
        search: false,
        pageSize: 20,
        pageSizeOptions: [20],
    }

    onChangeDep = (value) => {
        this.DepInput = value;
    }

    onSearchClick = () => {
        this.tableRef.current && this.tableRef.current.onQueryChange();
    }

    getDataFromServer = (query) => {
        return new Promise((resolve, reject) => {
            Department.getDepartment()
            .then(value => {
                if(value){
                    console.log(value)
                }
                else {
                    resolve({
                        data: [],
                        page: 0,
                        totalCount:0,
                     })
                }
            })
        })
    }

    render() {
        return(
            <Fragment>
                <div>

                </div>
                <div style={{width: '40%'}}> 
                <Input 
                title={' Bộ phận/ Phòng ban'}
                onChange = {this.onChangeDep} />
                <Button 
                title ={'Tìm kiếm'}
                onClick = {this.onSearchClick}
                />

                </div>
                <MaterialTable
                columns = {this.columnsTable}
                options = {this.optionsTable}
                icons = {TableIcons}
                data = {this.getDataFromServer}
                />
            </Fragment>

        )
    }
}
