import React, { Fragment } from 'react';
import MaterialTable from 'material-table';
import TableIcons from '../../common/materialicon';
import Input from '../../common/input';
import Button from '../../common/button';
import Department from '../../../core/department';
import ResponseCode from '../../../staticresources/responsecode'


export default class DepartmentManagement extends React.Component {

    DepInput = '';

    constructor(props) {
        super(props);
        this.state = {
            headerColor: 'black',
        };
        this.tableRef = React.createRef();
    }

    columnsTable = [
        {
            title: 'Tên', 
            field: 'dName',
        },
    
    ]

    optionsTable = {
        search: false,
        pageSize: 20,
        pageSizeOptions: [20],
        showTitle: false,
        actionsColumnIndex: -1

    }
    
    actions = [
        {
            icon: () => <TableIcons.Edit/>,
            tooltip: 'Chỉnh sửa thông tin',
            onClick: (event, rowData) => alert("You want Edit " + rowData.name)
        },
        rowData => ({
            icon: () => <TableIcons.Delete />,
            tooltip: 'Xóa',
            onClick: (event, rowData) => alert("You want to delete " + rowData.name),
        })
    ]

    onChangeDep = (value) => {
        this.DepInput = value;
    }

    onSearchClick = () => {
        this.tableRef.current && this.tableRef.current.onQueryChange();
    }

    onMouseOverHeader = () => {
        this.setState({
            headerColor: 'red'
        })
    }

    onMouseLeaveHeader = () => {
        this.setState({
            headerColor: 'black'
        })
    }

    getDataFromServer = (query) => {
        return new Promise((resolve, reject) => {
            Department.getDepartment(0, this.DepInput, query.page +1)
            .then(value => {
                if(value){
                    resolve({
                        data: value.items,
                        page: value.currentPage - 1,
                        totalCount: value.total,
                     })
                }
                else {
                    resolve({
                        data: [],
                        page: 0,
                        totalCount:0,
                     })
                }
            })
        })
    }

    onEditTableRow = (newData, oldData) => {
        console.log(newData);
        console.log(oldData);
        return new Promise((resolve, reject) => {
            Department.editDepartment(oldData.dId, newData.name)
                .then(res => {
                    if (res) {
                        if (res.code && res.code === ResponseCode.modify_dep_success) {
                            resolve();
                        }
                    }
                    resolve();
                })
        });
    }


    render() {

        let headerStyle = {
            color: this.state.headerColor,
        }

        return(
            <Fragment>
                <div style={styles.mainContainer}>
                    <h2>
                    Quản lý bộ phận / phòng ban
                    </h2>
                    <h2 onMouseOver={this.onMouseOverHeader}
                        onMouseLeave={this.onMouseLeaveHeader}
                        style={headerStyle}> Quản lý bộ phận / phòng ban
                    </h2>

                </div>
                <div className ="around">
                    <div className= "title-style">
                            <span> 
                            Quản lý bộ phận / phòng ban
                            </span>
                    </div>
                    <div  className="div-control" style={{width: '40%', display: 'flex'}}> 
                        <Input 
                        title={' Bộ phận/ Phòng ban'}
                        onChange = {this.onChangeDep} />
                        <Button 
                        title ={'Tìm kiếm'}
                        onClick = {this.onSearchClick}
                        />
                    </div>
                    <MaterialTable
                    tableRef = {this.tableRef}
                    columns = {this.columnsTable}
                    options = {this.optionsTable}
                    icons = {TableIcons}
                    data = {this.getDataFromServer}
                    title ={'Danh sách bộ phận/ Phòng ban '}
                    // actions={this.actions}
                    editable={{
                        onRowUpdate: this.onEditTableRow,
                    }}
                    />
                </div>
            </Fragment>

        )
    }
}

const styles = {
    mainContainer: {
        margin: '30px 10px 40px 20px',

    },

    searchContainer: {
        display: 'flex'
    },

    searchInput: {
        flex: 1,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        paddingRight: 10,
    }
}
