import React, { Fragment } from 'react';
import MaterialTable from 'material-table';
import TableIcons from '../../common/materialicon';
import Useage from '../../../core/user';
import Department from '../../../core/department';



export default class MapUserLockerManagement extends React.Component {
    dictDev = {};
    columnsTablle1 = [
        { 
            title: 'Họ và tên', 
            field: 'name'
        },
        { 
            title: 'ID nhân sự', 
            field: 'eCode'
        },
        { 
            title: 'Email',
             field: 'email' },
        { 
            title: 'Bộ phận làm việc', 
            field: 'dId', 
            render: rowData =>{
                if(rowData.dId % 2 === 0 )
                {
                    return (
                        <span style={{color: 'red'}}> 
                            {this.dictDev[rowData.dId]}
                        </span>)
                }
                else {
                    return (
                        <span style={{color: 'blue'}}> 
                            {this.dictDev[rowData.dId]}
                        </span>)
                }
            }
          },
        {
            title: 'Tình trạng phân tủ',
            field: 'gStatus',
            render: rowData =>
            {
                if (rowData.gStatus === 0) {
                    return(
                        <span style={{color: 'red'}}>
                            Chưa được phân tủ
                        </span> 
                    )
                }
                else {
                    return(
                        <span style={{color: 'green'}}>
                            Đã được phân tủ
                        </span>
                    )
                }
            }
        },
        {
            title: 'Action', 
            field: ''
        },
    ]
    constructor(props) {
        super(props);
        this.state = {
            isUpdateView: false, 
        };
      
    }
 
    
    optionsTable1 = {
        search: false,
        pageSize: 20,
        pageSizeOptions: [20],
    }

    dataFromServer = (query) =>
    {
        return new Promise((resolve, reject) => {
            Useage.getUsage('','','',0,0,1)                
            .then(value => {
                resolve({
                    data: value.items,
                    page: value.currentPage - 1,
                    totalCount: value.total,
                })                 
                 })
        })
    }

    componentDidMount() {
        Department.getDepartment()
            .then(value => {
                if(value) {
                    if(value.items.length >0) {
                        value.items.map((item, index) => {
                            {this.dictDev[item.dId] = item.dName}
                        })
                    }
                    this.setState({
                        isUpdateView: !this.isUpdateView,
                    });
                }
       })
    }

    render() {
        return(
            <Fragment>
                <MaterialTable
                icons ={TableIcons}
                options = {this.optionsTable1}
                data = { this.dataFromServer}
                columns = {this.columnsTablle1}
                title= 'Danh sách người dùng hiện tại'/>
                <MaterialTable
                icons ={TableIcons}
                title= 'Danh sách các tủ hiện tại'
                />

            </Fragment>
        )
    }
}
