import React, { Fragment } from 'react';
import User from '../../../core/user'
import MaterialTable from 'material-table'
import TableIcons from '../../common/materialicon'
import Input from '../../common/input'
import Button from '../../common/button'
import Combobox from '../../common/combobox'
import Department from '../../../core/department'

export default class UserManagement extends React.Component {
    NameInput = '';
    IdNSInput = '';
    EmailInput = '';
    RcodeInput = '';
    dictDev = {};
    isGroup =0;
    isPIN = 0;
    isTag = 0;
    Dep = {};
    dataTest=[
        'orange',
        'red',
        'blue',
        'purple'
      ];

    columns = [
        { 
            title: 'Họ và tên', 
            field: 'name'
        },
        { 
            title: 'ID nhân sự', 
            field: 'eCode'
        },
        { 
            title: 'Email',
             field: 'email' },
        { 
            title: 'Bộ phận làm việc', 
            field: 'dId', 
            render: rowData =>{
                if(rowData.dId % 2 === 0 )
                {
                    return (
                        <span style={{color: 'red'}}> 
                            {this.dictDev[rowData.dId]}
                        </span>)
                }
                else {
                    return (
                        <span style={{color: 'blue'}}> 
                            {this.dictDev[rowData.dId]}
                        </span>)
                }
            }
          },
        {
            title: 'Tình trạng PINCode', 
            field:'pCode',
            render: rowData => 
            {
                if(rowData.pCode === 0) {
                    return(
                        <span syle={{color: 'red'}}>
                            Chưa có
                        </span>
                    )
                }
                else {
                    return(
                        <span syle={{color: 'green'}}>
                            Đã có
                        </span>
                    )
                }
            }
        },
        {
            title: 'Thẻ từ đang sử dụng', 
            field: 'rCode'
        },
        {
            title: 'Tình trạng phân tủ',
            field: 'gStatus',
            render: rowData =>
            {
                if (rowData.gStatus === 0) {
                    return(
                        <span style={{color: 'red'}}>
                            Chưa được phân tủ
                        </span> 
                    )
                }
                else {
                    return(
                        <span style={{color: 'green'}}>
                            Đã được phân tủ
                        </span>
                    )
                }
            }
        },
        {
            title: 'Action', 
            field: ''
        },
    ]

    options1 = {
        search: false,
        pageSize: 20,
        pageSizeOptions: [20],
    }

    constructor(props) {
        super(props);
        this.state = {
            isUpdateView: false,
        };
        this.tableRef = React.createRef();
        this.modalRef = React.createRef();
    }
    componenWillMount() {
        console.log(this.dataTest);
    }

    onNameChange = (value) => {
        this.NameInput = value;
    }

    onIdNSChange = (value) => {
        this.IdNSInput = value;
    }

    onEmailChange = (value) => {
        this.EmailInput = value;
    }

    onRcodeChange = (value) => {
        this.RcodeInput = value;
    }

    onChangeIsPIN = (value) => {
        if (value === 'Đã có PINCode') {
            this.isPIN = 2;
        }
        else {
            this.isPIN = 1;
        }
    }

    onChangeIsGroup = (value) => {
        if (value === 'Chưa được phân tủ') {
            this.isGroup= 1;
        }
        else {
            this.isGroup = 2;
        }
    } 

    onChangeIsTag = (value) => {
        if (value === 'Chưa có thẻ') {
            this.isTag= 1;
        }
        else {
            this.isTag = 2;
        }
    }

    onChangeDep = (value) => {
        this.Dep = value;
    }

    onSearchClick = () => {
        this.tableRef.current && this.tableRef.current.onQueryChange();
        // if (typeof this.tableRef.current !== 'undefined'){
        //     this.tableRef.current.onQueryChange();
        // }
    }

    componentDidMount() {
        Department.getDepartment()
            .then(value => {
                if(value) {
                    if(value.items.length >0) {
                        value.items.map((item, index) => {
                            {this.dictDev[item.dId] = item.dName
                            this.Dep[index] = item.dName}
                        })
                    }
                    this.setState({
                        isUpdateView: !this.isUpdateView,
                    });
            }
       })
       const dataT = this.Dep.slice
       console.log(this.dataT);
        
    }

    getDepartmentOption =(query) => {
        return new Promise((resolve, reject) => {
            Department.getDepartment()                
            .then(value => {
                    resolve({
                        data: value.items.dName,
                    })               
                 })
        })
    }


    getDataFromServer = (query) => {
        return new Promise((resolve, reject) => {
            User.getUser(0, this.NameInput, this.IdNSInput, this.EmailInput, this.RcodeInput, this.isTag, this.isPIN, this.isGroup, query.page + 1)
                .then(value => {
                    resolve({
                        data: value.items,
                        page: value.currentPage - 1,
                        totalCount: value.total,
                    })               
             })
        })
    }

   
    render() {
        return (
            <Fragment>
                <div style={{padding: '10px'}}>
                    <h2> Quản lý người dùng</h2>
                </div>
                <div style={{display: 'flex' }}>
                    <Input 
                        title={'Tên nhân viên'}
                        onChange={this.onNameChange} 
                    />
                    <Input 
                        title={'ID nhân sự'} 
                        onChange={this.onIdNSChange} 
                    />
                    <Combobox
                     title={'Bộ phận làm việc'} 
                     data = {this.Dep.slice}
                     />
                    <Input 
                        title={'Email'} 
                        onChange={this.onEmailChange} 
                    />
                    <Input 
                        title={'Mã thẻ từ'} 
                        onChange = {this.onRcodeChange
                    }
                    />
                    <Combobox 
                        title={'Tình trạng sử dụng thẻ'}
                        data= {[
                            'Chưa có thẻ',
                            'Đã có thẻ',
                        ]}
                        onChange = {this.onChangeIsTag}
                    />                   
                </div>
                <div  style={{display: 'flex', width: '50%'}}>
                <Combobox 
                 title={'Tình trạng phân tủ'}
                 onChange={this.onChangeIsGroup}
                 data = {[
                    'Chưa được phân tủ',
                    'Đã được phân tủ',
                ] }
                 />
                <Combobox 
                title={'Tình trạng PINCode'}
                data = { [
                        'Đã có PINCode',
                        'Chưa có PINCode',
                    ] }
                onChange = {this.onChangeIsPIN}
                />
                <Button title={'Tìm kiếm'}
                        onClick={this.onSearchClick} />
                </div>
            
                <div style={{ maxWidth: '100%' }}>
                    <MaterialTable
                        tableRef={this.tableRef}
                        options={this.options1}
                        icons={TableIcons}
                        columns={this.columns}
                        data={this.getDataFromServer}
                        title='Danh sách người dùng hiện tại'
                    />
                </div>
            </Fragment>
        )
    }
}
