import React, { Fragment } from 'react';
import MaterialTable from 'material-table'
import TableIcons from '../../common/materialicon'
import Locker from '../../../core/locker'
import Building from '../../../core/building'
import Level from '../../../core/level'
import Controller from '../../../core/controller';
import Combobox from '../../common/combobox';
import Input from '../../common/input';
import Button from '../../common/button';

export default class LockerManagement extends React.Component {
    rBuildingIdInput = 0;
    rLevelIdInput = 0;
    rImeiInput = '';
    rLabelInput = '';
    lPage = [];
    bDatas = [];
    lDatas = [];
    cDatas = [];

    DictBName = {};
    Build = [];
    DictBId = {};
    DictLvName = {};
    DictLvId ={};
    Level =[];
    DictBLvIName = {};
    DictBLvIId = {};
    Imei = [];
    Test1 = [];
    constructor(props) {
        super(props);
        this.state = {
            isShowLock : false,
            BIdInput: 0,
            LvIdInput: 0,
            BLvIInput: '',
            LabelInput: '',
            Test: [],


        };
        this.tableRef = React.createRef();

    }

    onChangeBLvI = (value) => {
        this.setState({
            BLvIInput: this.DictBLvIId[value]
        })
    }

    onChangeBLevel = (value) => {
        this.setState(
            {
             LvIdInput: this.DictLvId[value]

            }
        )
    }

    onChangeBuild = (value) => {
        this.setState(
            {
                BIdInput: this.DictBId[value]

            }
        )
    } 

    onLabelChange = (value) => {
        this.setState(
            {
                LabelInput: value,
            }
        )
    }

    onSearchClick = () => {
   //     this.tableRef.current && this.tableRef.current.onQueryChange();
        this.setState(
            {
                isShowLock: true,
            }
        );
       // console.log(this.state.Test);

    }


    componentWillUpdate() {
//        console.log('update')
        this.Test1 = [];
    
       Locker.getLocker(this.state.BIdInput, this.state.LvIdInput, this.state.BLvIInput, this.state.LabelInput, 0,0, 1)
            .then(value => {
                if(value) {
                    if(value.items.length >0) {
                        value.items.map((item, index) => {
                            this.Test1[index] = item.lLb;
                           
                        })                    
                    }
                    this.setState({
                        Test: this.Test1 ,
                    })
                }
                        
        })


    }

    componentDidMount() {
 //       this.setState({isShowLock: false})
        Building.getBuilding()
            .then(value => {
                if(value) {
                    if(value.items.length >0) {
                        value.items.map((item,index) => {
                            this.DictBName[item.bId] = item.bName;
                            this.DictBId[item.bName] = item.bId;
                            this.Build[index] = item.bName; 
                        })
                    }
                } 
        })
        
        Level.getLevel() 
        .then(value => {
            if(value) {
                if(value.items.length >0) {
                    value.items.map((item,index) => {
                        let bLv = ' Tòa ' + item.bName + ' - Tầng '  + item.lLv;
                        this.DictLvName[item.lId] = bLv;
                        this.DictLvId[bLv] = item.lId;
                        this.Level[index] = bLv ;
                    })
                    
                }
            }
        })

        Controller.getController()
        .then(value => {
            if(value) {
                if(value.items.length >0) {
                    value.items.map((item, index) => {
                        let bLvImei =' Tòa ' + item.bName + ' - Tầng '  + item.lLv + '- quản lý cụm tủ: ' + item.sLabel + ' đến ' +  item.eLabel;
                        this.DictBLvIName[item.imei] = bLvImei;
                        this.DictBLvIId[bLvImei] = item.imei;
                        this.Imei[index] = bLvImei;
                    })                    
                }
            }
        })  

    }
    
    render() {
        let ManageLocker = null;
        if(this.state.isShowLock === true) {
            if(this.state.Test){
                ManageLocker = this.state.Test.map((item, index)=> {
                    return(
                    <div className="test">
                        {item}
                    </div>
                    ) 
    
                 });
            }
           
        }
                
        
        return(
            <Fragment>
                <div style ={{margin: '30px 10px 40px 20px'}}>
                  <h2>
                      Quản lý tủ
                  </h2>
                </div>

                <div className ="around">

                    <div className= "title-style">
                            <span> 
                                Danh sách các tòa nhà trong hệ thống
                            </span>
                    </div>
                    <div className="div-control" style ={{display: 'flex'}}>
                        <Combobox
                            title={'Tòa nhà'} 
                            data = {this.Build}
                            onChange = {this.onChangeBuild}
                        />

                        <Combobox
                        title={'Tầng'}
                        data = {this.Level}
                        onChange = {this.onChangeBLevel}
                        
                        />

                        <Combobox
                        title={'Thiết bị quản lý'} 
                        data = { this.Imei}
                        onChange = { this.onChangeBLvI }
                        />

                        <Input 
                            title={'Nhãn tủ'} 
                            onChange = {this.onLabelChange
                        }
                        />

                        <Button 
                        title ={'Tìm kiếm'}
                        onClick = {this.onSearchClick}
                        />
                    </div>

                    <div>
                        <div className="div-lock">
                        {ManageLocker}
                        </div>
                        
                    </div>
                </div>
            </Fragment>
        )
    }
}
