import React, { Fragment } from 'react';
import MaterialTable from 'material-table';
import LokerStatusReport from '../../../core/report';
import TableIcons from '../../common/materialicon';
import Input from '../../common/input';
import Combobox from '../../common/combobox';
import Button from '../../common/button';


export default class LockerStatusReport extends React.Component {
    labelInput = '';
    constructor(props) {
        super(props);
        this.state = {
        };
        this.tableRef = React.createRef();
    }
    columns = [
        { title: 'Tòa nhà', field: 'bName' },
        { title: 'Tầng', field: 'lLv' },
        { title: 'Tủ', field: 'lLabel' },
        { title: 'Trạng thái', field: 'status', render: rowData => {
            if(rowData.status === 'FREE') {
                return (
                    <span style={{color: 'green'}}>
                        Tủ trống
                    </span>
                )
            }
            else {
                return (
                    <span style={{color: 'red'}}>
                        Tủ không trống
                    </span>
                )
            }
        } },
        { title: 'Người sử dụng cuối cùng', field: 'eName' },
        { title: 'ID nhân sự', field: 'eCode' },
        { title: 'Thời điểm thao tác lần cuối', field: 'aTime' },
    ]

    options = {
        search: false,
        pageSize: 20,
        pageSizeOptions: [20],

    }

    getDataFromServer = (query) => {
        return new Promise((resolve, reject) => {
            LokerStatusReport.getStatusReport(0,0, '', this.labelInput, 0, query.page + 1 )
                .then(value => {
                    resolve({
                        data: value.items,
                        page: value.currentPage - 1,
                        totalCount: value.total,
                    }) 
                })
        })
    }

    onLabelChange = (value) => 
    { 
        this.labelInput = value;
    }

    onSearchClick = () => {
        this.tableRef.current && this.tableRef.current.onQueryChange();
        // if (typeof this.tableRef.current !== 'undefined'){
        //     this.tableRef.current.onQueryChange();
        // }
    }


    componentDidMount() {
    }

    render() {
        return(
            <Fragment>
                <div style={{padding: '10px'}}>
                    <h3>Báo cáo trạng thái các tủ đang hoạt động trong hệ thống</h3>
                </div>
                <div style={{display: 'flex', margin: '10px' }}>
                    <Combobox
                     classes = {'combobox-style'}
                     title={'Tòa nhà'} 
                     />
                    <Combobox
                     title={'Tầng'} 
                     />
                    <Combobox 
                    title={'Thiết bị quản lí'} 
                    />
                    <Input 
                        title={'Nhãn tủ'} 
                        onChange = {this.onLabelChange
                    }
                    />
                    <Combobox title={'Tình trạng sử dụng tủ'} />   
                    <Button title={'Tìm kiếm'}
                        onClick={this.onSearchClick} />
                </div>
                <MaterialTable 
                tableRef={this.tableRef}
                title = 'Danh sách các tủ đang hoạt động trong hệ thống'
                columns = {this.columns}
                options = {this.options}
                icons= {TableIcons}
                data = {this.getDataFromServer}
                />
            </Fragment>
        )
    }
}
