import React, { Fragment } from 'react';
import MaterialTable from 'material-table';
import TableIcons from '../../common/materialicon';
import Input from '../../common/input';
import Building from '../../../core/building'

export default class BuildingManagement extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            isUpdateView: false,
        };
        this.tableRef = React.createRef();
       
    }

    columnsTable = [
        {
            title: 'Tòa nhà',
            field: ''
        },
        {
            title: 'Địa chỉ',
            field: ''
        },
        {
            title: 'Thông tin mô tả',
            field: ''
        },
        {
            title: 'Action',
            field: '',
        }
    ]

    optionsTable = {
        search: false,
        pageSize: 20,
        pageSizeOptions: [20],
    }

    getDataFromServer = (query) => {
        Building.getBuilding()
        .then(value => {
            console.log(value);
        })
    }

    render() {
        return(
            <Fragment>
                <div syle ={{padding: '10px'}}>
                    Quản lý tòa nhà trong hệ thống 
                </div>
                <MaterialTable
                options={this.optionsTable}
                icons = {TableIcons}
                data = {this.getDataFromServer}
                title = {'Danh sách các tòa nhà trong hệ thống'}
                />
            </Fragment>
        );
    }

}
