import React, { Fragment } from 'react';
import MaterialTable from 'material-table'
import TableIcons from '../../common/materialicon'
import Locker from '../../../core/locker'
import Building from '../../../core/building'
import Level from '../../../core/level'
import Controller from '../../../core/controller';
import Combobox from '../../common/combobox';
import Input from '../../common/input';
import Button from '../../common/button';

export default class LockerManagement extends React.Component {
    rBuildingIdInput = 0;
    rLevelIdInput = 0;
    rImeiInput = '';
    rLabelInput = '';
    lPage = [];
    bDatas = [];
    lDatas = [];
    cDatas = [];

    DictBName = {};
    Build = [];
    DictBId = {};
    BIdInput = 0;

    DictLvName = {};
    DictLvId ={};
    Level =[];
    LvIdInput = 0;

    DictBLvIName = {};
    DictBLvIId = {};
    Imei = [];
    BLvIInput = '';

    LabelInput = '';

    Test = [];

    optionsTable = {
        search: false,
        showTitle: false,
        pageSize: 20,
        pageSizeOptions: [20],
    }


    columns  = [
        {
            title: 'Tòa nhà',
            field: '',
        },
        {
            title: 'Tầng',
            field: '',
        },
        {
            title: 'Thiết bị quản lý'
        },
        {
            title: 'Nhãn tủ',
            field: '',
        },
        
    ]
    constructor(props) {
        super(props);
        this.state = {
            isShowLock : false,
        };
        this.tableRef = React.createRef();

    }

    onChangeBLvI = (value) => {
        this.BLvIInput = this.DictBLvIId[value];
    }

    onChangeBLevel = (value) => {
        this.LvIdInput = this.DictLvId[value];
    }

    onChangeBuild = (value) => {
        this.BIdInput = this.DictBId[value];
    } 

    onLabelChange = (value) => {
        this.LabelInput = value;
    }

    onSearchClick = () => {
   //     this.tableRef.current && this.tableRef.current.onQueryChange();
        this.setState(
            {
                isShowLock: true,
            }
        );
        console.log(this.Test);

    }

    componentDidMount() {
        Building.getBuilding()
            .then(value => {
                if(value) {
                    if(value.items.length >0) {
                        value.items.map((item,index) => {
                            this.DictBName[item.bId] = item.bName;
                            this.DictBId[item.bName] = item.bId;
                            this.Build[index] = item.bName; 
                        })
                    }
                } 
        })
        
        Level.getLevel() 
        .then(value => {
            if(value) {
                if(value.items.length >0) {
                    value.items.map((item,index) => {
                        let bLv = ' Tòa ' + item.bName + ' - Tầng '  + item.lLv;
                        this.DictLvName[item.lId] = bLv;
                        this.DictLvId[bLv] = item.lId;
                        this.Level[index] = bLv ;
                    })
                    
                }
            }
        })

        Controller.getController()
        .then(value => {
            if(value) {
                if(value.items.length >0) {
                    value.items.map((item, index) => {
                        let bLvImei =' Tòa ' + item.bName + ' - Tầng '  + item.lLv + '- quản lý cụm tủ: ' + item.sLabel + ' đến ' +  item.eLabel;
                        this.DictBLvIName[item.imei] = bLvImei;
                        this.DictBLvIId[bLvImei] = item.imei;
                        this.Imei[index] = bLvImei;
                    })                    
                }
            }
        })

        Locker.getLocker(this.BIdInput, this.LvIdInput, this.BLvIInput, this.LabelInput, 0,0, 2)
            .then(value => {
                if(value) {
                    if(value.items.length >0) {
                        value.items.map((item, index) => {
                            this.Test[index] = item.lLb;
                        })                    
                    }
                }
                        
            })
            
    }
    
    getDataFromServer = (query) => {
        return new Promise((resolve, reject) => {
            Locker.getLocker(this.BIdInput, this.LvIdInput, this.BLvIInput, this.LabelInput, 0,0, query.page +1)
            .then(value => {
                resolve({
                    data: value.items,
                    page: value.currentPage - 1,
                    totalCount: value.total,
                 })          
             })
        })
    }

    render() {
        let ellement = null;
        let ManageLocker = null;
        if(this.state.isShowLock === true) {
            ManageLocker = this.Test.map((item, index)=> {
                return(
                <div className="test">
                    {item.lLb}
                </div>
                ) 
                console.log(item.Llb)

             });
        }
                
        
        return(
            <Fragment>
              <div style={{padding: '10px'}}>
                  <h2>
                      Quản lý tủ
                  </h2>
              </div>
              <div style ={{display: 'flex'}}>
                    <Combobox
                        title={'Tòa nhà'} 
                        data = {this.Build}
                        onChange = {this.onChangeBuild}
                    />

                    <Combobox
                    title={'Tầng'}
                    data = {this.Level}
                    onChange = {this.onChangeBLevel}
                    
                    />

                    <Combobox
                    title={'Thiết bị quản lý'} 
                    data = { this.Imei}
                    onChange = { this.onChangeBLvI }
                    />

                    <Input 
                        title={'Nhãn tủ'} 
                        onChange = {this.onLabelChange
                    }
                    />

                    <Button 
                    title ={'Tìm kiếm'}
                    onClick = {this.onSearchClick}
                    />
                </div>

                <div>
                    <div className="div-lock">
                     {ManageLocker}
                    </div>
                    
                </div>
            </Fragment>
        )
    }
}
