import React, { Fragment } from 'react';
import MaterialTable from 'material-table';
import TableIcons from '../../common/materialicon';
import Input from '../../common/input';
import Button from '../../common/button';
import Building from '../../../core/building';
import Level from '../../../core/level';
import Controller from '../../../core/controller';
import Combobox from '../../common/combobox';
import DeleteOutline from '@material-ui/icons/DeleteOutline';
import Extension from '@material-ui/icons/Extension'


export default class ControllerManagemeent extends React.Component {

    DictBName = {};
    Build = [];
    DictBId = {};
    BIdInput = 0; 

    DictLvName = {};
    DictLvId ={};
    Level =[];
    LvIdInput = 0;

    ImeiInput = '';
    MACInput = '';


    constructor(props) {
        super(props);
        this.state = {
        };
        this.tableRef = React.createRef();
       
    }

    columnsTable = [
        {
            title: 'Tòa nhà',
            field: 'bName'
        },
        {
            title: 'Tầng',
            field: 'lLv', 
            render: rowDate => {
                return(
                    <span>
                        Tầng {rowDate.lLv}
                    </span>
                )
            }
        },
        {
            title: 'Imei',
            field: 'imei',
        },
        {
            title: 'Địa chỉ MAC',
            field: 'mac',
        },
        {
            title: 'Khu vực',
            field: 'zone',
        },
        {
            title: 'Thời điểm phản hồi cuối cùng',
            field: 'health',
        },
        {
            title: 'Phiên bản Firmware',
            field: '',
        },
        
    ]

    optionsTable = {
        search: false,
        pageSize: 20,
        pageSizeOptions: [20],
        showTitle: false,
        actionsColumnIndex: -1,
    }

    onChangeBLevel = (value) => {
        this.LvIdInput = this.DictLvId[value];
    } 

    onChangeBuild = (value) => {
        this.BIdInput = this.DictBId[value];
    }

    onChangeImeiInpput = (value) => {
        this.ImeiInput = value;
    }

    onChangeMACInput  = (value) => {
        this.MACInput = value;
    }

    onSearchClick = () => {
        this.tableRef.current && this.tableRef.current.onQueryChange();
    }

    getDataFromServer = (query) => {
        return new Promise((resolve, reject) => {
            Controller.getController(this.BIdInput, this.LvIdInput, this.ImeiInput, this.MACInput,'', query.page +1)
            .then(value => {
                if(value){
                    resolve({
                        data: value.items,
                        page: value.currentPage - 1,
                        totalCount: value.total,
                     })
                }
                else {
                    resolve({
                        data: [],
                        page: 0,
                        totalCount:0,
                     })
                }
            })
        })
    }

    componentDidMount() {
        Building.getBuilding()
            .then(value => {
                if(value) {
                    if(value.items.length >0) {
                        value.items.map((item,index) => {
                            this.DictBName[item.bId] = item.bName;
                            this.DictBId[item.bName] = item.bId;
                            this.Build[index] = item.bName; 
                        })
                    }
                } 
        })

        Level.getLevel()
        .then(value => {
            if(value){
                if(value.items.length >0) {
                    value.items.map((item, index) => {
                        let bLv = ' Tòa ' + item.bName + ' - Tầng '  + item.lLv;
                        this.DictLvName[item.lId] = bLv;
                        this.DictLvId[bLv] = item.lId;
                        this.Level[index] = bLv ;
                    }) 
                }
            }
        });
    }


    render() {
        return(
            <Fragment>
                <div style ={{margin: '30px 10px 40px 20px'}}>
                    <h2>Quản lý thiết bị điều khiển</h2>
                </div>
                <div  className ="around">
                    <div className= "title-style">
                        <span>
                            Danh sách thiết bị điều khiển hiện tại
                        </span>
                    </div>
                    <div   className="div-control" style={{width: '85%', display: 'flex'}}>
                        <Combobox
                            title={'Tòa nhà'} 
                            data = {this.Build}
                            onChange = {this.onChangeBuild}
                        /> 
                        <Combobox
                            title={'Tầng'}
                            data = {this.Level}
                            onChange = {this.onChangeBLevel}
                        />
                        <Input 
                            title= {'Mã thiết bị'}
                            onChange = {this.onChangeImeiInpput}
                        />
                        <div className="space-button"></div>
                        <Input 
                            title ={'Địa chỉ MAC'}
                            onChange = {this.onChangeMACInput}
                        />
                        <Button
                            title={'Tìm kiếm'}
                            onClick= {this.onSearchClick}
                        />

                    </div>
                    <MaterialTable
                    tableRef = {this.tableRef}
                    columns = {this.columnsTable}
                    options={this.optionsTable}
                    icons = {TableIcons}
                    data = {this.getDataFromServer}
                    actions={[
                        {
                          icon: () => <Extension/>,
                          tooltip: 'Thêm tủ cho nhân viên',
                          onClick: (event, rowData) => alert("You want Edit " )
                        },
                        rowData => ({
                          icon: () => <DeleteOutline/>,
                          tooltip: 'Xóa',
                          onClick: (event, rowData) => alert("You want to delete " ),
                        })
                      ]}
                    />
                </div>

            </Fragment>
        );
    }
}
