import React, { Fragment } from 'react';
import MaterialTable from 'material-table';
import TableIcons from '../../common/materialicon';
import Input from '../../common/input';
import Button from '../../common/button';
import Building from '../../../core/building';
import Level  from '../../../core/level';
import Combobox from '../../common/combobox';


export default class LevelManagement extends React.Component {

    DictBName = {};
    Build = [];
    DictBId = {};
    BIdInput = 0;

    constructor(props) {
        super(props);
        this.state = {
        };
        this.tableRef = React.createRef();
       
    }

    columnsTable = [
        {
            title: 'Tòa nhà',
            field: 'bName'
        },
        {
            title: 'Tầng',
            field: 'lLv', 
            render: rowDate => {
                return(
                    <span>
                        Tầng {rowDate.lLv}
                    </span>
                )
            }
        },
        {
            title: 'Thông tin chi tiết',
            field: 'lDes'
        },
        {
            title: 'Action',
            field: '',
        }
    ]

    optionsTable = {
        search: false,
        pageSize: 20,
        pageSizeOptions: [20],
    }

    getDataFromServer = (query) => {
        return new Promise((resolve, reject) => {
            Level.getLevel(this.BIdInput, query.page +1)
            .then(value => {
                if(value){
                    resolve({
                        data: value.items,
                        page: value.currentPage - 1,
                        totalCount: value.total,
                     })
                }
                else {
                    resolve({
                        data: [],
                        page: 0,
                        totalCount:0,
                     })
                }
            })
        })
    }

    onChangeBuildInput = (value) => {
        this.BIdInput = this.DictBId[value];
    }
    onClickSearch = () => {
        this.tableRef.current && this.tableRef.current.onQueryChange();
    }

    componentDidMount() {
        Building.getBuilding()
            .then(value => {
                if(value) {
                    if(value.items.length >0) {
                        value.items.map((item,index) => {
                            this.DictBName[item.bId] = item.bName;
                            this.DictBId[item.bName] = item.bId;
                            this.Build[index] = item.bName; 
                        })
                    }
                } 
        })
    }

    render () {
        return(
            <Fragment>
                <div>

                </div>
                <div style={{width: '40%', display: 'flex'}}>
                    <Combobox 
                    title={'Tòa'}
                    data = {this.Build}
                    onChange={this.onChangeBuildInput}

                    />
                    <Button 
                    title ={'Tìm kiếm'}
                    onClick = {this.onClickSearch}
                    />
                </div>
                <MaterialTable
                 tableRef = {this.tableRef}
                 columns = {this.columnsTable}
                 options={this.optionsTable}
                 icons = {TableIcons}
                 data = {this.getDataFromServer}
                 title = {'Danh sách các tầng nhà trong hệ thống'}
                />
            </Fragment>
        );
    }
}
