import React, { Fragment } from 'react';
import MaterialTable from 'material-table';
import TableIcons from '../../common/materialicon';
import Input from '../../common/input';
import Combobox from '../../common/combobox';
import Button from '../../common/button';
import Warning from '../../../core/warning';
import Building from '../../../core/building';

export default class WarningManagement extends React.Component {
    DictBuilding = {};

    columnsTable = [
        { title: 'Loại cảnh báo', field: 'tID', render: rowData => {
            if (rowData.tID === 2) {
                return(
                    <span style ={{color: 'red'}}>
                        Thiết bị mất kết nối
                    </span>
                )
            }
            else {
                return(
                    <span style ={{color: 'green'}}>
                        truy cập tủ trái phép
                    </span>
                )
            }

        } },
        { title: 'Hình thức', field: 'method', render: rowData => {
            if (rowData.tID === 2) {
                return(
                    <span>
                        
                    </span>
                )
            }
            else {
                return(
                    <span style ={{color: 'green'}}>
                        Không rõ
                    </span>
                )
            }

        } },
        { 
            title: 'Tòa nhà', 
            field: 'bId',
            render: rowData => {
                return(
                    <span>{this.DictBuilding[rowData.bId]}</span>
                )
            } 
        },
        { 
            title: 'Tầng', 
            field: 'lLv' 
        },
        { 
            title: 'Tủ', 
            field: 'lLabel' 
        },
        { 
            title: 'Thiết bị quản lý',
             field: 'imei' 
        },
        { 
            title: 'Thời gian ghi nhận',
             field: 'wTime' 
        },
    ] 
    optionsTable = {
        search: false,
        pageSize: 20,
        pageSizeOptions: [20],
    }

    constructor(props) {
        super(props);
        this.state = {
            isUpdateView: false,
        };
        this.tableRef = React.createRef();
       
    }
    
    getDataFromServer = (query) => {
        return new Promise((resolve, reject) => {
            Warning.getWarning(0,0,'','','',0,0,'','', query.page +1)
                .then(value => {
                    resolve({
                        data: value.items,
                        page: value.currentPage - 1,
                        totalCount: value.total,
                    })  
                })
        })
    }

    componentDidMount() {
        Building.getBuilding()
        .then(value => {
            if(value){
                if(value.items.length >0) {
                    value.items.map((item, index) => {
                        {this.DictBuilding[item.bId] = item.bName}
                    }) 
                }
                this.setState({
                    isUpdateView: !this.isUpdateView,
                });
            }
        })
    }

    
    render() {
        return(
            <Fragment>
                Cảnh báo hệ thống
                <MaterialTable 
                tableRef={this.tableRef}
                options = {this.optionsTable}
                title= 'Danh sách các cảnh báo hệ thống'
                icons= {TableIcons}
                data = {this.getDataFromServer}
                columns = {this.columnsTable}
                />
            </Fragment>
        )
    }
}
